import React, { Component } from 'react';
import { Form } from 'react-bootstrap';
import { Editor } from "@tinymce/tinymce-react";
import { Link } from 'react-router-dom';


export class EditRight extends Component {
    state = {
        startDate: new Date()
    };

    render() {
        const styles = {
            image: {
                height: 70,
                marginRight: 20
            },

        };

        const product = {
            username: "User Fullname",
            id: " 13t77fvtf4 ",
            imageUrl: "../../assets/images/faces/face1.jpg",
            price: 77.99,
            category: "Product category",
            amount: 245
        }
        const categories_list = () => {
            let categories = []
            for (let i = 0; i < 6; i++) {
                categories.push(
                    <option>Category {i + 1}</option>
                )
            }
            return categories;

        }
        const type_list = () => {
            let types = []
            for (let i = 0; i < 6; i++) {
                types.push(
                    <option>Type {i + 1}</option>
                )
            }
            return types;

        }
        return (
            <>
                <div class=" card ">
                    <div class=" card-body row">
                        <img style={styles.image} src={require("../../assets/images/faces/face1.jpg")} alt="user icon" />

                        {/* <img src={require(product.imageUrl)} /> */}
                        <div>
                            <p>   <Link to="/right" class="text-muted">Manage Rights</Link> / User details</p>
                            <h3>{product.username}</h3>
                        </div>
                    </div>
                </div>

                <div className="card mt-3">
                    <div className="card-body">
                        <h4 className="card-title">User Information</h4>
                        <form className="form-sample">
                            <p className="card-description"> id:  {product.id}  </p>
                            <div className="row">
                                <div className="col-md-6">
                                    <div class="form-group">
                                        <div class="form-check">
                                            <label class="form-check-label">
                                                <input type="checkbox" class="form-check-input" />
                                                <i class="input-helper"></i>Manager rights (product: read/add/edit/remove) (order: read)
                                            </label>
                                        </div>
                                        <div class="form-check">
                                            <label class="form-check-label">
                                                <input type="checkbox" class="form-check-input" checked="" />
                                                <i class="input-helper"></i>Checked
                                            </label>
                                        </div>

                                    </div>
                                </div>

                            </div>

                        </form>
                    </div>
                </div>

            </>

        )
    }
}

export default EditRight;
